import React, { Component } from 'react'
import {detailProduct} from './data'
import { runInThisContext } from 'vm';

const ProductContext = React.createContext();

class ProductProvider extends Component {
constructor(props) {
  super(props);
  this.state = {
    products: [],
    filtered: [],
    detailProduct: {},
    cart: [],
    modalOpen: false,
    modalProduct: {},
    cartSubTotal: 0,
    cartTax: 0,
    cartTotal: 0
  }

  this.handleChange = this.handleChange.bind(this);
}

componentDidMount(){
  fetch('https://backendapi.turing.com/products')
  .then(results => {
    return results.json();
  }).then(data => {
    this.setState({
      products: data.rows, 
      filtered: data.rows,
      detailProduct: {},
      modalProduct: data.rows[0]});

  });
  this.setProducts();
}


setProducts = () => {
  let tempProducts = [];

  this.state.products.forEach(item=> {
    const singleItem = {...item};
    tempProducts = [...tempProducts, singleItem];
  })
  this.setState(()=> {
    return {products: tempProducts};
  })
}

getItem = (id) => {
  const product = this.state.filtered.find(item=>  item.id == id);
  return product;
}

handleDetail = (id) => {
  const product = this.getItem(id);
  this.setState(
    ()=>{
      return {detailProduct: product};
    }
  )
}

addToCart = (id) => {
    let tempProducts = [...this.state.products];
    const product = this.getItem(id);
    const index = tempProducts.indexOf(product);
    product.inCart = true;
    product.count = 1;
    const price = parseInt(product.price);
    product.total = price;

    this.setState(()=>{
      return {products:tempProducts, cart:[...this.state.cart, product]}
    },
    ()=>{
      this.addTotals();
    })
}

openModal = (id) => {
  const product = this.getItem(id);
  this.setState(()=>{
    return {modalProduct: product, modalOpen:true};
  })
}

closeModal = () => {
    this.setState(()=>{
        return {modalOpen: false};
    })
}

increment = (id) => {
  let tempCart = [...this.state.cart];
  const selectedProduct = tempCart.find(item=>item.id === id);

  const index = tempCart.indexOf(selectedProduct);
  const product = tempCart[index];

  product.count = product.count + 1;
  product.total = product.count * product.price;

  this.setState(
    ()=> {
      return {cart: [...tempCart]};
    },
    ()=> {
      this.addTotals();
    }
  );
}

decrement = (id) => {
  let tempCart = [...this.state.cart];
  const selectedProduct = tempCart.find(item=>item.id === id);

  const index = tempCart.indexOf(selectedProduct);
  const product = tempCart[index];
  product.count = product.count -1;

  if(product.count === 0){
    this.removeItem(id);
  }else {
    product.total = product.count * product.price;
    this.setState(
      ()=>{
        return {cart: [...tempCart]};
      },
      ()=>{
        this.addTotals();
      }
    )
  }
}

removeItem = (id) => {
  let tempProducts = [...this.state.products];
  let tempCart = [...this.state.cart];

  tempCart = tempCart.filter(item=>item.id!==id);

  const index = tempProducts.indexOf(this.getItem(id));
  let removedProduct = tempProducts[index];
  removedProduct.inCart = false;
  removedProduct.count = 0;
  removedProduct.total = 0;

  this.setState(()=>{
    return {
      cart: [...tempCart],
      products: [...tempProducts]
    }
  }, ()=>{
    this.addTotals();
  })
}

clearCart = () => {
  this.setState(()=>{
    return {cart:[]};
  }, ()=>{
    this.setProducts();
    this.addTotals(); 
  })
}

addTotals = () => {
    let subTotal = 0;
    this.state.cart.map(item => (subTotal += item.total));
    const tempTax= subTotal * 0.1;
    const tax = parseFloat(tempTax.toFixed(2));
    const total = subTotal + tax;
    this.setState(()=>{
      return {
        cartSubTotal: subTotal,
        cartTax: tax,
        cartTotal: total
      }
    })
}

handleChange(e) {
  let newList = [];

  if (e.target.value !== "") {
    newList = this.state.products.filter(item => {
      const lc = item.name.toLowerCase();
      const filter = e.target.value.toLowerCase();

      return lc.includes(filter);
    });
  } else {   
    newList = this.state.products;
  }

  this.setState({
    filtered: newList
  });
}

render() {
  return (
    <ProductContext.Provider value={
        {
            ...this.state, 
            handleDetail: this.handleDetail,
            addToCart: this.addToCart,
            openModal: this.openModal,
            closeModal: this.closeModal,
            increment: this.increment,
            decrement: this.decrement,
            removeItem: this.removeItem,
            clearCart: this.clearCart,
            handleChange: this.handleChange
        }
    }>
        {this.props.children}
    </ProductContext.Provider>
  );
}
}

const ProductConsumer = ProductContext.Consumer;

export {ProductProvider, ProductConsumer};
